scriptlet.script:
returnCode = scriptletRawResult['returnCode'];
errorMessage = scriptletRawResult['stdErr'];

if (returnCode == -1) {
 	if (errorMessage == "") {
		if (scriptletRawResult["exception"] == "") {
			errorMessage = scriptletRawResult["stdOut"];
		} else {
			errorMessage = scriptletRawResult["exception"];
		}
	}
}

scriptletContext.put("errorMessage", errorMessage);
